
public class TestForLoop {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int l = 0;
		for(int i=0; i< 10; i++){
			System.out.println(l++);
			
			
		}
		System.out.println();
		int k = 0;
		for(int j=0; j< 10; ++j){
			System.out.println(++k);
			
			
		}
			
	}

}
